CREATE TABLE customer (	id BIGINT(20) NOT NULL AUTO_INCREMENT,	address VARCHAR(255) NULL DEFAULT NULL,	email VARCHAR(255) NULL DEFAULT NULL,	name VARCHAR(255) NULL DEFAULT NULL,	phone_number VARCHAR(255) NULL DEFAULT NULL,	PRIMARY KEY (id))
CREATE TABLE request (	id BIGINT(20) NOT NULL AUTO_INCREMENT,	quantity INT(11) NULL DEFAULT NULL,	id_customer BIGINT(20) NULL DEFAULT NULL, PRIMARY KEY (id),	INDEX (id_customer),CONSTRAINT FOREIGN KEY (id_customer) REFERENCES customer (id))